#!/bin/bash

# Root User Config
ROOT_PW='vagrant'
Root_SSH='ssh-rsa  vagrant insecure public key'

# Vagrant User Config
VAGRANT_PW='vagrant'
VAGRANT_SSH='ssh-rsa  vagrant insecure public key'

# Root Actual Configuration
mkdir -p /root/.ssh/
echo $ROOT_SSH >> /root/.ssh/authorized_keys
echo root:$ROOT_PW | /usr/sbin/chpasswd

# Actual Vagrant Configuration
mkdir -p /home/vagrant/.ssh
echo $VAGRANT_SSH >> /home/vagrant/.ssh/authorized_keys
chown vagrant:vagrant /home/vagrant/.ssh/authorized_keys
chown vagrant:vagrant /home/vagrant/.ssh
echo vagrant:$VAGRANT_PW | /usr/sbin/chpasswd

# OpenSSH Config
mkdir /etc/ssh/old_keys
mv /etc/ssh/ssh_host_* /etc/ssh/old_keys
dpkg-reconfigure openssh-server
sed -i 's/*PermitRootLogin*/PermitRootLogin\ without-password/g' /etc/ssh/sshd_config
sed -i 's/*UseDNS*/UseDNS\ no/g' /etc/ssh/sshd_config
